import React, { useReducer, useState } from 'react';

// Reducer function
const availableTimesReducer = (state, action) => {
    switch (action.type) {
        case 'INITIALIZE_TIMES':
            return action.times;
        case 'UPDATE_TIMES':
            // For now, just return the same times regardless of date
            return [
                '17:00', '18:00', '19:00', '20:00', '21:00', '22:00'
            ];
        default:
            return state;
    }
};

function BookingForm({ availableTimes, dispatch }) {
    // Define state variables for each form field
    const [date, setDate] = useState('');

    // Event handlers for form fields
    const handleDateChange = (e) => {
        const selectedDate = e.target.value;
        setDate(selectedDate);
        // Dispatch action to update available times based on selected date
        dispatch({ type: 'UPDATE_TIMES', date: selectedDate });
    };

    const handleTimeChange = (e) => {
        // Handle time selection
    };

    const handleGuestsChange = (e) => {
        // Handle guests selection
    };

    const handleOccasionChange = (e) => {
        // Handle occasion selection
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Perform form validation here
        if (date && time && guests && occasion) {
            // Handle reservation submission here
            console.log('Reservation submitted:', { date, time, guests, occasion });
        } else {
            alert('Please fill out all fields.');
        }
    };

    return (
        <form style={{ display: 'grid', maxWidth: '200px', gap: '20px' }} onSubmit={handleSubmit}>
            <label htmlFor="res-date">Choose date</label>
            <input
                type="date"
                id="res-date"
                value={date}
                onChange={handleDateChange}
                required
            />

            <label htmlFor="res-time">Choose time</label>
            <select
                id="res-time"
                value={time}
                onChange={handleTimeChange}
                required
            >
                <option value="">Select a time</option>
                {availableTimes.map((time) => (
                    <option key={time} value={time}>{time}</option>
                ))}
            </select>

            <label htmlFor="guests">Number of guests</label>
            <input
                type="number"
                placeholder="1"
                min="1"
                max="10"
                id="guests"
                value={guests}
                onChange={handleGuestsChange}
                required
            />

            <label htmlFor="occasion">Occasion</label>
            <select
                id="occasion"
                value={occasion}
                onChange={handleOccasionChange}
                required
            >
                <option value="">Select an occasion</option>
                <option value="Birthday">Birthday</option>
                <option value="Anniversary">Anniversary</option>
            </select>

            <input type="submit" value="Make Your Reservation" />
        </form>
    );
}

export default BookingForm;
